<?phpclass ControllerPaymentbank10 extends Controller {	private $error = array();	public function index() {		$this->load->language('payment/bank10');		$this->document->setTitle($this->language->get('doc_title'));		$this->load->model('setting/setting');		if (($this->request->server['REQUEST_METHOD'] == 'POST') && $this->validate()) {			$this->model_setting_setting->editSetting('bank10', $this->request->post);			$this->session->data['success'] = $this->language->get('text_success');			$this->response->redirect($this->url->link('extension/payment', 'token=' . $this->session->data['token'], 'SSL'));		}		$data['heading_title'] = $this->language->get('heading_title');				$data['text_edit'] = $this->language->get('text_edit');		$data['text_enabled'] = $this->language->get('text_enabled');		$data['text_disabled'] = $this->language->get('text_disabled');		$data['entry_pin'] = $this->language->get('entry_pin');		$data['entry_id'] = $this->language->get('entry_id');		$data['entry_order_status'] = $this->language->get('entry_order_status');		$data['entry_status'] = $this->language->get('entry_status');		$data['entry_sort_order'] = $this->language->get('entry_sort_order');		$data['button_save'] = $this->language->get('button_save');		$data['button_cancel'] = $this->language->get('button_cancel');		if (isset($this->error['warning'])) {			$data['error_warning'] = $this->error['warning'];		} else {			$data['error_warning'] = '';		}		if (isset($this->error['pin'])) {			$data['error_pin'] = $this->error['pin'];		} else {			$data['error_pin'] = '';		}		$data['breadcrumbs'] = array();		$data['breadcrumbs'][] = array(			'text' => $this->language->get('text_home'),			'href' => $this->url->link('common/dashboard', 'token=' . $this->session->data['token'], 'SSL')		);		$data['breadcrumbs'][] = array(			'text' => $this->language->get('text_payment'),			'href' => $this->url->link('extension/payment', 'token=' . $this->session->data['token'], 'SSL')		);		$data['breadcrumbs'][] = array(			'text' => $this->language->get('heading_title'),			'href' => $this->url->link('payment/bank10', 'token=' . $this->session->data['token'], 'SSL')		);		$data['action'] = $this->url->link('payment/bank10', 'token=' . $this->session->data['token'], 'SSL');		$data['cancel'] = $this->url->link('extension/payment', 'token=' . $this->session->data['token'], 'SSL');		if (isset($this->request->post['bank10_PIN'])) {			$data['bank10_PIN'] = $this->request->post['bank10_PIN'];		} else {			$data['bank10_PIN'] = $this->config->get('bank10_PIN');		}		if (isset($this->request->post['bank10_ID'])) {			$data['bank10_ID'] = $this->request->post['bank10_ID'];		} else {			$data['bank10_ID'] = $this->config->get('bank10_ID');		}		if (isset($this->request->post['bank10_order_status_id'])) {			$data['bank10_order_status_id'] = $this->request->post['bank10_order_status_id'];		} else {			$data['bank10_order_status_id'] = $this->config->get('bank10_order_status_id');		}		$this->load->model('localisation/order_status');		$data['order_statuses'] = $this->model_localisation_order_status->getOrderStatuses();		if (isset($this->request->post['bank10_status'])) {			$data['bank10_status'] = $this->request->post['bank10_status'];		} else {			$data['bank10_status'] = $this->config->get('bank10_status');		}		if (isset($this->request->post['bank10_sort_order'])) {			$data['bank10_sort_order'] = $this->request->post['bank10_sort_order'];		} else {			$data['bank10_sort_order'] = $this->config->get('bank10_sort_order');		}		$data['header'] = $this->load->controller('common/header');		$data['column_left'] = $this->load->controller('common/column_left');		$data['footer'] = $this->load->controller('common/footer');		$this->response->setOutput($this->load->view('payment/bank10.tpl', $data));	}	protected function validate() {		if (!$this->user->hasPermission('modify', 'payment/bank10')) {			$this->error['warning'] = $this->language->get('error_permission');		}				if (!$this->request->post['bank10_PIN']) {			$this->error['pin'] = $this->language->get('error_pin');		}				if (!$this->request->post['bank10_ID']) {			$this->error['id'] = $this->language->get('error_pin');		}		return !$this->error;	}}?>